mapper.update:
UPDATE QNA
SET qna_writer =
#{qna_writer}, qna_title = #{qna_title}, qna_content = #{qna_content},
qna_password =
#{qna_password}, qna_date = SYSDATE,  qna_category_id = #{qna_category_id}
WHERE qna_id = #{qna_id}

mapper.select:
SELECT qna_category_id AS qna_category_id
FROM QNA_CATEGORY
WHERE qna_type = #{qna_type}

SELECT qna_password
FROM QNA
WHERE qna_id = #{qna_id}
